import React from "react";
export const GetPokemonDetail = ({match}) => {
    const [pokemonImage,setPokemonImage] = React.useState(null)
    const Element = () =>{
        return [<h2>{match.params.type}</h2>,<img src={pokemonImage}/>]
    }
    React.useEffect(()=>{
        fetch("https://pokeapi.co/api/v2/pokemon/bulbasaur")
        .then(res=>res.json())
        .then(data=>setPokemonImage(data.sprites.front_shiny));
    },[])
    React.useEffect(()=>{
        console.log(pokemonImage)
    },[pokemonImage])
    return (
        <>
        <h2>{match ? <Element/> : null}</h2>
        </>
    )
}
export const PokemonDetail = () => {
    return(
        <>
        <h1>Pokemon Detail</h1>
        <GetPokemonDetail/>
        </>
    )
};
