import React from 'react';
import { Link } from "react-router-dom";

export const Container = (prop) => {
    const {children} = prop;
    return (
    <div className="card-container">
        {children}
    </div>
    )
}
const PokemonCard = (dataset) => {
    const [pokemonDetail, setPokemonDetail] = React.useState([])

    React.useEffect(()=>{
        console.log(dataset.url)
        fetch(dataset.url)
        .then(res=>res.json())
        .then(data=>setPokemonDetail(data));
        // console.log(pokemon)
    },[])
    React.useEffect(()=>{
        // console.log(dataset.url)
        console.log(pokemonDetail)
    },[pokemonDetail])
    if(dataset){
        // console.log(dataset)

        return (
                <div className="card-container">
                    <img src={pokemonDetail ? pokemonDetail.sprites.other['official-artwork'].front_default : null} alt={pokemonDetail ? pokemonDetail.sprites.other['official-artwork'].front_default : null}/>
                    <p><Link to={"/pokemon/"+dataset.name}>{dataset.name}</Link></p>
                    {/* <Route path="/pokemon/:type" component={GetPokemonDetail}/> */}
                </div>
        )
    }
    return {Container}
};

export default PokemonCard;
