import React from "react";
import {AppContext} from "../App";
import {useCookies} from "react-cookie";



export const MyPokemonList = () => {
    const [cookies,setCookie] = useCookies(['name',"mypokemon"]);

    const {state,dispatch} = React.useContext(AppContext);
    console.log(state)
    const onDeleteClick = (ev) => {
        console.log(ev.target.getAttribute('data-name'))
        console.log(cookies.mypokemon)
        // console.log(cookies.mypokemon)
        var del = cookies.mypokemon.filter(a=>a!==ev.target.getAttribute('data-name'))
        setCookie("mypokemon",JSON.stringify(del),{path:"/"})
        dispatch({type:"set",hash: del})
    }
    React.useEffect(()=>{

    },[cookies.mypokemon])
    return(
        <>
        <h1>MyPokemonList</h1>
        {state.session ? state.session.map((item,idx) => {
            return (
                <>
                <p key={idx}>{item}</p>
                <button key={idx+item} data-name={item} onClick={onDeleteClick} > delete</button>
                </>
            )
        }) : null}

        </>
    )
}
